import React, { useState } from 'react';

const ConfirmationPage = ({ cosmetic, onSubmit, onBack }) => {
  const [notificationMethod, setNotificationMethod] = useState('email');
  const [contactInfo, setContactInfo] = useState('');
  const [isSubmitting, setIsSubmitting] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!contactInfo.trim()) return;

    setIsSubmitting(true);

    // Simulate API call
    await new Promise(resolve => setTimeout(resolve, 1000));

    onSubmit({
      cosmetic,
      notificationMethod,
      contactInfo,
      timestamp: new Date().toISOString()
    });

    setIsSubmitting(false);
  };

  const getLastSeenText = () => {
    const daysAgo = cosmetic.lastSeen || 30;
    if (daysAgo === 0) return 'Currently in shop';
    if (daysAgo === 1) return 'Yesterday';
    return `${daysAgo} days ago`;
  };

  return (
    <div className="container">
      <div className="card">
        <button className="btn btn-secondary mb-2" onClick={onBack}>
          ← Back to Search
        </button>

        <h1 className="text-center mb-2">Confirm Your Selection</h1>

        <div className="confirmation-content">
          <div className="cosmetic-details">
            <h2 className="mb-1">{cosmetic.name}</h2>
            <div className="cosmetic-image">
              {cosmetic.type === 'skin' && '👤'}
              {cosmetic.type === 'emote' && '💃'}
              {cosmetic.type === 'pickaxe' && '⛏️'}
            </div>

            <div className="detail-item">
              <span className="detail-label">Type:</span>
              <span className="detail-value">{cosmetic.type}</span>
            </div>
            <div className="detail-item">
              <span className="detail-label">Rarity:</span>
              <span className={`detail-value rarity-${cosmetic.rarity}`}>
                {cosmetic.rarity}
              </span>
            </div>
            <div className="detail-item">
              <span className="detail-label">Last Seen:</span>
              <span className="detail-value">{getLastSeenText()}</span>
            </div>
            <div className="detail-item">
              <span className="detail-label">VBucks Cost:</span>
              <span className="detail-value">{cosmetic.price || '800'}</span>
            </div>
            <div className="detail-item">
              <span className="detail-label">Season Released:</span>
              <span className="detail-value">{cosmetic.season || 'Chapter 3'}</span>
            </div>
          </div>

          <div>
            <h3 className="mb-1">Set Up Notifications</h3>
            <p className="mb-2">
              We'll notify you as soon as {cosmetic.name} returns to the item shop.
            </p>

            <form onSubmit={handleSubmit}>
              <div className="notification-options">
                <div
                  className={`notification-option ${notificationMethod === 'email' ? 'selected' : ''}`}
                  onClick={() => setNotificationMethod('email')}
                >
                  <div className="option-icon">📧</div>
                  <div className="option-title">Email</div>
                  <div className="option-description">Get email notifications</div>
                </div>

                <div
                  className={`notification-option ${notificationMethod === 'sms' ? 'selected' : ''}`}
                  onClick={() => setNotificationMethod('sms')}
                >
                  <div className="option-icon">📱</div>
                  <div className="option-title">SMS</div>
                  <div className="option-description">Get text messages</div>
                </div>
              </div>

              <div className="form-group">
                <label className="form-label">
                  {notificationMethod === 'email' ? 'Email Address' : 'Phone Number'}
                </label>
                <input
                  type={notificationMethod === 'email' ? 'email' : 'tel'}
                  className="form-input"
                  placeholder={
                    notificationMethod === 'email' 
                      ? 'your@email.com' 
                      : '+1 (555) 123-4567'
                  }
                  value={contactInfo}
                  onChange={(e) => setContactInfo(e.target.value)}
                  required
                />
              </div>

              <button 
                type="submit" 
                className="btn btn-success" 
                disabled={isSubmitting}
              >
                {isSubmitting ? (
                  <>
                    <div className="loading"></div>
                    Setting up notifications...
                  </>
                ) : (
                  'Confirm & Set Up Notifications'
                )}
              </button>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ConfirmationPage;
