const API_BASE_URL = 'https://fortniteapi.io/v2/items';
const API_KEY = process.env.REACT_APP_FN_API_KEY || 'your-api-key-here';

// Common rarities mapping
const rarityMap = {
  'common': 'common',
  'uncommon': 'uncommon', 
  'rare': 'rare',
  'epic': 'epic',
  'legendary': 'legendary',
  'mythic': 'legendary',
  'transcendent': 'legendary'
};

// Allowed cosmetic types - ONLY these will be shown
const ALLOWED_TYPES = {
  'outfit': 'skin',
  'emote': 'emote', 
  'pickaxe': 'pickaxe',
  'backpack': 'backbling'
};

export const searchCosmetics = async (searchTerm, type = 'all') => {
  console.log('=== ðŸ” API DEBUG START ===');
  console.log('Search term:', searchTerm);
  console.log('Type:', type);
  console.log('API Key exists:', !!API_KEY);
  console.log('API Key value:', API_KEY === 'your-api-key-here' ? 'MOCK KEY' : '***' + API_KEY.slice(-4));

  try {
    // If no API key, fall back to mock data
    if (API_KEY === 'your-api-key-here') {
      console.warn('âš ï¸ USING MOCK DATA - No API key configured');
      console.log('ðŸ’¡ Add your API key to .env file as: REACT_APP_FN_API_KEY=your_key_here');
      const mockResults = await mockSearchCosmetics(searchTerm, type);
      console.log('Mock results:', mockResults.length, 'items');
      return mockResults;
    }

    console.log('ðŸŒ Making API request to:', `${API_BASE_URL}/list?lang=en`);

    const response = await fetch(`${API_BASE_URL}/list?lang=en`, {
      headers: {
        'Authorization': API_KEY
      }
    });

    console.log('ðŸ“¡ Response status:', response.status);
    console.log('ðŸ“¡ Response status text:', response.statusText);
    console.log('ðŸ“¡ Response ok:', response.ok);

    if (!response.ok) {
      // Get more details about the error
      const errorText = await response.text();
      console.error('âŒ Response error text:', errorText);

      if (response.status === 401) {
        throw new Error(`API Error 401: Unauthorized - Invalid API key. Check your key at fortniteapi.io`);
      } else if (response.status === 403) {
        throw new Error(`API Error 403: Forbidden - API key may be invalid or expired`);
      } else if (response.status === 429) {
        throw new Error(`API Error 429: Rate limited - Too many requests`);
      } else {
        throw new Error(`API request failed with status ${response.status}: ${response.statusText}. Response: ${errorText}`);
      }
    }

    const data = await response.json();
    console.log('âœ… API Response received');
    console.log('Response result:', data.result);
    console.log('Total items count:', data.items?.length);

    if (!data.items) {
      console.error('âŒ No items array in response');
      console.log('Full response:', data);
      throw new Error('No items found in API response');
    }

    // Filter items based on search, type, and ONLY allowed cosmetic types
    const filteredItems = data.items.filter(item => {
      const isAllowedType = ALLOWED_TYPES.hasOwnProperty(item.type.id);
      const matchesSearch = item.name.toLowerCase().includes(searchTerm.toLowerCase());

      // Additional type filtering if specific type is selected
      let matchesType = true;
      if (type !== 'all') {
        if (type === 'skin') {
          matchesType = item.type.id === 'outfit';
        } else if (type === 'emote') {
          matchesType = item.type.id === 'emote';
        } else if (type === 'pickaxe') {
          matchesType = item.type.id === 'pickaxe';
        } else if (type === 'backbling') {
          matchesType = item.type.id === 'backpack';
        }
      }

      return isAllowedType && matchesSearch && matchesType;
    });

    console.log('Filtered items count:', filteredItems.length);
    console.log('Allowed types:', Object.keys(ALLOWED_TYPES));

    // Transform API data to our app format
    const transformedItems = filteredItems.slice(0, 20).map(item => ({
      id: item.id,
      name: item.name,
      type: mapItemType(item.type.id),
      rarity: rarityMap[item.rarity.id] || 'common',
      imageUrl: item.images.icon,
      lastSeen: calculateDaysSinceLastSeen(item),
      price: item.price ? item.price : 800,
      description: item.description,
      introduction: item.introduction?.text || 'Unknown season',
      rarityName: item.rarity?.name || 'Common'
    }));

    console.log('Transformed items:', transformedItems.length);
    console.log('Item types found:', [...new Set(transformedItems.map(item => item.type))]);
    console.log('=== âœ… API DEBUG END ===');

    return transformedItems;

  } catch (error) {
    console.error('âŒ FINAL API ERROR:', error.message);
    console.error('Error stack:', error.stack);
    console.log('ðŸ”„ Falling back to mock data');

    // Fall back to mock data
    const mockResults = await mockSearchCosmetics(searchTerm, type);
    console.log('Fallback mock results:', mockResults.length, 'items');
    return mockResults;
  }
};

export const getCosmeticById = async (id) => {
  try {
    if (API_KEY === 'your-api-key-here') {
      return mockGetCosmeticById(id);
    }

    const response = await fetch(`${API_BASE_URL}/get?id=${id}`, {
      headers: {
        'Authorization': API_KEY
      }
    });

    if (!response.ok) {
      throw new Error(`API request failed with status ${response.status}`);
    }

    const data = await response.json();

    // Check if this is an allowed type
    if (!ALLOWED_TYPES[data.item.type.id]) {
      throw new Error('Item type not allowed');
    }

    return {
      id: data.item.id,
      name: data.item.name,
      type: mapItemType(data.item.type.id),
      rarity: rarityMap[data.item.rarity.id] || 'common',
      imageUrl: data.item.images.icon,
      lastSeen: calculateDaysSinceLastSeen(data.item),
      price: data.item.price || 800,
      description: data.item.description,
      introduction: data.item.introduction?.text || 'Unknown season',
      rarityName: data.item.rarity?.name || 'Common'
    };

  } catch (error) {
    console.error('API Error:', error);
    return mockGetCosmeticById(id);
  }
};

// Helper functions
const mapItemType = (apiType) => {
  return ALLOWED_TYPES[apiType] || 'other';
};

const calculateDaysSinceLastSeen = (item) => {
  if (!item.added) return 30; // Default fallback

  const addedDate = new Date(item.added);
  const today = new Date();
  const diffTime = Math.abs(today - addedDate);
  const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));

  return Math.min(diffDays, 365); // Cap at 1 year
};

// Fallback mock data (only allowed types)
const mockCosmetics = [
  // Skins
  { id: '1', name: 'Renegade Raider', type: 'skin', rarity: 'rare', lastSeen: 45, price: 1200, description: 'Elite combat specialist', introduction: 'Season 1' },
  { id: '2', name: 'Aerial Assault Trooper', type: 'skin', rarity: 'rare', lastSeen: 60, price: 1200, description: 'High-altitude warrior', introduction: 'Season 1' },
  { id: '3', name: 'Ghoul Trooper', type: 'skin', rarity: 'epic', lastSeen: 15, price: 1500, description: 'Spooky seasonal outfit', introduction: 'Season 1' },
  { id: '4', name: 'Skull Trooper', type: 'skin', rarity: 'epic', lastSeen: 30, price: 1500, description: 'Bone-chilling combatant', introduction: 'Season 1' },

  // Emotes
  { id: '5', name: 'Orange Justice', type: 'emote', rarity: 'rare', lastSeen: 25, price: 800, description: 'Popular dance move', introduction: 'Season 4' },
  { id: '6', name: 'Floss', type: 'emote', rarity: 'rare', lastSeen: 40, price: 800, description: 'Classic dance emote', introduction: 'Season 2' },
  { id: '7', name: 'Take the L', type: 'emote', rarity: 'epic', lastSeen: 10, price: 800, description: 'Taunt your opponents', introduction: 'Season 3' },
  { id: '8', name: 'Scenario', type: 'emote', rarity: 'epic', lastSeen: 5, price: 800, description: 'Show off your moves', introduction: 'Season 5' },

  // Pickaxes
  { id: '9', name: 'Reaper', type: 'pickaxe', rarity: 'rare', lastSeen: 35, price: 800, description: 'Harvesting tool', introduction: 'Season 3' },
  { id: '10', name: 'AC/DC', type: 'pickaxe', rarity: 'rare', lastSeen: 50, price: 800, description: 'Rock out while harvesting', introduction: 'Season 6' },
  { id: '11', name: 'Vision', type: 'pickaxe', rarity: 'epic', lastSeen: 15, price: 1200, description: 'Futuristic harvesting tool', introduction: 'Season X' },
  { id: '12', name: 'Ice Breaker', type: 'pickaxe', rarity: 'legendary', lastSeen: 2, price: 1500, description: 'Chilly harvesting tool', introduction: 'Season 7' },

  // Back Blings
  { id: '13', name: 'Black Shield', type: 'backbling', rarity: 'epic', lastSeen: 20, price: 800, description: 'Protective back bling', introduction: 'Season 3' },
  { id: '14', name: 'Royale Dragon', type: 'backbling', rarity: 'legendary', lastSeen: 8, price: 1200, description: 'Mythical dragon companion', introduction: 'Season 8' },
  { id: '15', name: 'Iron Cage', type: 'backbling', rarity: 'rare', lastSeen: 30, price: 500, description: 'Metallic back accessory', introduction: 'Season 4' }
];

