import React, { useState } from 'react';
import Header from './components/Header';
import SearchForm from './components/SearchForm';
import ConfirmationPage from './components/ConfirmationPage';
import SuccessPage from './components/SuccessPage';
import './styles/App.css';

// Temporary API Test Component - Remove after debugging
const APITest = () => {
  const testDirectAPI = async () => {
    const API_KEY = process.env.REACT_APP_FN_API_KEY;

    console.log('=== 🧪 DIRECT API TEST ===');
    console.log('API Key loaded:', !!API_KEY);
    console.log('API Key value:', API_KEY ? '***' + API_KEY.slice(-4) : 'MISSING');
    console.log('Full API Key:', API_KEY); // This will show your actual key

    try {
      console.log('🌐 Making request to Fortnite API...');
      const response = await fetch('https://fortniteapi.io/v2/items/list?lang=en', {
        headers: {
          'Authorization': API_KEY
        }
      });

      console.log('📡 Response status:', response.status);
      console.log('📡 Response status text:', response.statusText);
      console.log('📡 Response ok:', response.ok);

      if (response.ok) {
        const data = await response.json();
        console.log('✅ SUCCESS! API is working!');
        console.log('Items found:', data.items?.length);
        console.log('API result:', data.result);
        console.log('First item:', data.items?.[0]?.name);

        // Show success message on page
        alert(`✅ API SUCCESS!\nFound ${data.items?.length} items\nFirst item: ${data.items?.[0]?.name}`);
      } else {
        const errorText = await response.text();
        console.log('❌ API FAILED! Error response:', errorText);

        if (response.status === 401) {
          alert('❌ API Error 401: Unauthorized\nYour API key is invalid or missing\nCheck your key at fortniteapi.io');
        } else if (response.status === 403) {
          alert('❌ API Error 403: Forbidden\nAPI key may be invalid or expired');
        } else if (response.status === 429) {
          alert('❌ API Error 429: Rate Limited\nToo many requests, try again later');
        } else {
          alert(`❌ API Error ${response.status}\nCheck browser console for details`);
        }
      }
    } catch (error) {
      console.log('❌ NETWORK ERROR:', error.message);
      alert(`❌ Network Error: ${error.message}\nCheck console for details`);
    }
  };

  const checkEnvironment = () => {
    console.log('=== 🔍 ENVIRONMENT CHECK ===');
    console.log('NODE_ENV:', process.env.NODE_ENV);
    console.log('REACT_APP_FN_API_KEY exists:', !!process.env.REACT_APP_FN_API_KEY);
    console.log('REACT_APP_FN_API_KEY length:', process.env.REACT_APP_FN_API_KEY?.length);
    console.log('REACT_APP_FN_API_KEY starts with:', process.env.REACT_APP_FN_API_KEY?.substring(0, 6) + '...');
  };

  return (
    <div style={{
      background: 'rgba(255,165,0,0.1)',
      padding: '1.5rem',
      margin: '1rem 0',
      border: '2px solid orange',
      borderRadius: '0.5rem'
    }}>
      <h3 style={{marginBottom: '1rem', color: 'orange'}}>🧪 API Diagnostics</h3>

      <div style={{display: 'flex', gap: '1rem', flexWrap: 'wrap', marginBottom: '1rem'}}>
        <button onClick={testDirectAPI} className="btn btn-primary">
          Test API Connection
        </button>
        <button onClick={checkEnvironment} className="btn btn-secondary">
          Check Environment
        </button>
      </div>

      <div style={{
        background: 'rgba(0,0,0,0.3)',
        padding: '1rem',
        borderRadius: '0.5rem',
        fontSize: '0.8rem'
      }}>
        <h4 style={{marginBottom: '0.5rem'}}>Current Status:</h4>
        <div><strong>API Key Loaded:</strong> {process.env.REACT_APP_FN_API_KEY ? '✅ Yes' : '❌ No'}</div>
        <div><strong>Key Length:</strong> {process.env.REACT_APP_FN_API_KEY?.length || '0'} characters</div>
        <div><strong>Environment:</strong> {process.env.NODE_ENV}</div>
        {!process.env.REACT_APP_FN_API_KEY && (
          <div style={{color: 'red', marginTop: '0.5rem'}}>
            ⚠️ No API key found. Create a .env file with REACT_APP_FN_API_KEY=your_key
          </div>
        )}
      </div>

      <p style={{fontSize: '0.8rem', marginTop: '0.5rem', color: 'var(--gray)'}}>
        Check browser console (F12) for detailed results
      </p>
    </div>
  );
};

function App() {
  const [currentPage, setCurrentPage] = useState('search');
  const [selectedCosmetic, setSelectedCosmetic] = useState(null);
  const [userData, setUserData] = useState(null);

  const handleCosmeticSelect = (cosmetic) => {
    setSelectedCosmetic(cosmetic);
    setCurrentPage('confirmation');
  };

  const handleNotificationSubmit = (data) => {
    setUserData(data);
    setCurrentPage('success');
  };

  const handleBackToSearch = () => {
    setCurrentPage('search');
    setSelectedCosmetic(null);
  };

  return (
    <div className="App">
      <Header />

      {/* Temporary API Test - Remove this after debugging */}
      <div className="container">
        <APITest />
      </div>

      <main className="main-content">
        {currentPage === 'search' && (
          <SearchForm onCosmeticSelect={handleCosmeticSelect} />
        )}
        {currentPage === 'confirmation' && (
          <ConfirmationPage
            cosmetic={selectedCosmetic}
            onSubmit={handleNotificationSubmit}
            onBack={handleBackToSearch}
          />
        )}
        {currentPage === 'success' && (
          <SuccessPage
            cosmetic={selectedCosmetic}
            userData={userData}
            onBack={handleBackToSearch}
          />
        )}
      </main>

      {/* Environment Warning */}
      {!process.env.REACT_APP_FN_API_KEY && (
        <div style={{
          position: 'fixed',
          bottom: '0',
          left: '0',
          right: '0',
          background: 'rgba(255,0,0,0.9)',
          color: 'white',
          padding: '1rem',
          textAlign: 'center',
          zIndex: 1000
        }}>
          ⚠️ API Key Missing: Create a .env file with REACT_APP_FN_API_KEY=your_key_here
        </div>
      )}
    </div>
  );
}

export default App;
